import { rerenderEntireTree } from "../render";

let state = {
    profilePage:{
        Posts:[
            {id: 1, messages: "Hi, how are you", likesCount: 15},
            {id: 2,messages: "it's my first post",likesCount: 20},
            ],
        newPostText: "Hello World10",
    },
    MessagesPage:{
        messagesData:[
            {id: 1, messages: "Hi"},
            {id: 2, messages: "How are you?"},
            {id: 3, messages: "What is your name?"},
        ],
        dialogsData:[
            {id: 1, name: "Dima"},
            {id: 2, name: "Ilyas"},
            {id: 3, name: "Vanya"},
            {id: 4, name: "Sasha"},
            {id: 5, name: "Maximilian"}
        ],
    }
}

export let addPost = (postMessagee) => {
    let newPost = {
        id:5,
        messages: postMessagee,
        likesCount:0,
    }

    state.profilePage.Posts.push(newPost);
    rerenderEntireTree(state);
};

export let UpdateNewPostText = (newText) => {
    state.profilePage.newPostText.push(newPost);
    rerenderEntireTree(state);
};

export default state;
