import { NavLink } from 'react-router-dom';
import DialogItem from './DialogItem/DialogItem';
import classes from './Dialogs.module.css'
import Message from './Message/Message';
import React from 'react'  ;
import { addMeassageActionCreater, updateNewTextMessageActionCreater } from '../../Redux/dialogs-reducer';

const Dialogs = (props) =>{

    let state = props.dialogsPage;
    
    let dialogsElements =state.dialogsData.map( dialog => <DialogItem name={dialog.name} key={dialog.id} id={dialog.id}/>);

    let messagesElements =state.messagesData.map( message => <Message message={message.messages} key={message.id}/>);

    let MessegeRef = React.createRef();

    let addMessage = () => {
        props.sendMessage();
    };

    let onMessageText = () =>{
        let text = MessegeRef.current.value;
        props.upadeNewMessageBody(text)
      };

    return(
        <div className={classes.dialogs}>
            <div className={classes.dialogsItems}>
                { dialogsElements }
            </div>
            <div className={classes.messages}>
                { messagesElements }
                <textarea ref={MessegeRef} value={state.MessageText} onChange={onMessageText}></textarea>
                <button onClick={addMessage}>Send</button>
            </div>
            
        </div>
    );
}

export default Dialogs;
