import classes from './MyPosts.module.css';
import Post from './Post/Post';
import React from 'react'  ;

const MyPosts = (Data) => {

  // console.log(Data.Data.Posts);
  // let Posts =[
  //   {id: 1, messages: "Hi, how are you", likesCount: 15},
  //   {id: 2,messages: "it's my first post",likesCount: 20},
  // ];

  let PostsData = Data.Data.Posts
                .map(post => <Post message={post.messages} like={post.likesCount} />)

  let newPostElement = React.createRef();

  let AddPost = () => {
    Data.addPost();
    Data.UpdateNewPostText("");
  }

  let onPostChange = () =>{
    let text = newPostElement.current.value;
    Data.UpdateNewPostText(text);

  }

  return (
    <div className={classes.postsBlock}>
      <h3>My Post</h3>
      <div>
        <div>
          <textarea ref={newPostElement} onChange={onPostChange} value={Data.newPostText}></textarea>
        </div>
        <div>
          <button onClick={AddPost}>add post</button>
        </div>
      </div>
      <div className={classes.posts}>
        { PostsData }
      </div>
    </div>
  );
}

export default MyPosts;
