const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';

let initialState = {
    users:[
        {id:1,photoUser:"./assets/img/defUserPng",followed:"false",name:"Ilyas",status:"ok",location:{city:"amster",country:"hamster"}},
        {id:2,photoUser:"./assets/img/defUserPng",followed:"true",name:"Ils",status:"o",location:{city:"amst",country:"hter"}},
        {id:3,photoUser:"./assets/img/defUserPng",followed:"false",name:"Ilya",status:"ok",location:{city:"amer",country:"hamer"}}
    ]
};

const usersReducer = (state = initialState ,action) => {
    switch(action.type){
        case FOLLOW:
            return{
                ...state,
                users:state.users.map(u => {
                    if(u.id === action.userID){
                        return {...u, followed:true}
                    }
                    return u;
                })
            }
        case UNFOLLOW:
            return{
                ...state,
                users:state.users.map(u => {
                    if(u.id === action.userID){
                        return {...u, followed:false}
                    }
                    return u;
                })
            }
        case SET_USERS:{
            return {...state, users:[...state.users, ...action.users]}
        }
        default:
            return state;
    }
}

export const followAC = (userID) => ({type: FOLLOW,userID})
export  const unfollowAC = (userID) =>({ type: UNFOLLOW,userID})
export  const setUsersAC = (users) =>({ type: SET_USERS,users})

export default usersReducer;
