import { NavLink } from 'react-router-dom';
import DialogItem from './DialogItem/DialogItem';
import classes from './Dialogs.module.css'
import Message from './Message/Message';
import React from 'react'  ;
import { addMeassageActionCreater, updateNewTextMessageActionCreater } from '../../Redux/dialogs-reducer';
import Dialogs from './Dialogs';
import {StoreContext} from 'react-redux';

const DialogsContainer = () =>{

    // let state = props.store.getState();

    // let addMessage = () => {
    //     props.store.dispatch(addMeassageActionCreater());
    // };
    
    // let onMessageText = (text) =>{
    //     let action = updateNewTextMessageActionCreater(text);
    //     props.store.dispatch(action);
    // };
    return(
        <StoreContext.Consumer>
            {(store) => {
                let state = store.getState();

                let addMessage = () => {
                    store.dispatch(addMeassageActionCreater());
                };
                
                let onMessageText = (text) =>{
                    let action = updateNewTextMessageActionCreater(text);
                    store.dispatch(action);
                };
                return(
                    <Dialogs upadeNewMessageBody={onMessageText} sendMessage={addMessage} dialogsPage={state.MessagesPage}/>
                );
            }
            }
            
        </StoreContext.Consumer>
        
    );
}

export default DialogsContainer;
