import dialogsReducer from "./dialogs-reducer";
import profileReducer from "./profile-reducer";

const ADD_POST = 'ADD-POST';
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT';
const ADD_MESSAGE = 'ADD-MESSAGE';
const UPDATE_NEW_MESSAGE_TEXT = 'UPDATE-NEW-MESSAGE-TEXT';


let store = {
    _state: {
        profilePage:{
            Posts:[
                {id: 1, messages: "Hi, how are you", likesCount: 15},
                {id: 2,messages: "it's my first post",likesCount: 20},
                ],
            newPostText: "Hello World10",
        },
        MessagesPage:{
            messagesData:[
                {id: 1, messages: "Hi"},
                {id: 2, messages: "How are you?"},
                {id: 3, messages: "What is your name?"},
            ],
            dialogsData:[
                {id: 1, name: "Dima"},
                {id: 2, name: "Ilyas"},
                {id: 3, name: "Vanya"},
                {id: 4, name: "Sasha"},
                {id: 5, name: "Maximilian"}
            ],
            MessageText: "Hi",
        },
        sidebar : {},
    },
    _CallSubscriber(){
        console.log("good");
    },
    GetState(){
        return this._state;
    },
    subscribe(observer){
        this._CallSubscriber = observer;
    },
    dispatch (action){
        this._state.profilePage = profileReducer(this._state.profilePage,action);
        this._state.MessagesPage = dialogsReducer(this._state.MessagesPage,action);
        this._CallSubscriber(this._state);
    },

    
}

export const addPostActionCreater = () => {
    return{type: ADD_POST}
  }
  
export  const updateNewTextActionCreater = (text) =>{
    return{ type: UPDATE_NEW_POST_TEXT, newText: text}
}

export let addMeassageActionCreater = () =>{
    return{type: ADD_MESSAGE}
} 

export let updateNewTextMessageActionCreater = (text) =>{
    return {type: UPDATE_NEW_MESSAGE_TEXT, newMessage:text}
}

export default store;
