const ADD_MESSAGE = 'ADD-MESSAGE';
const UPDATE_NEW_MESSAGE_TEXT = 'UPDATE-NEW-MESSAGE-TEXT';

let initialState = {
    messagesData:[
        {id: 1, messages: "Hi"},
        {id: 2, messages: "How are you?"},
        {id: 3, messages: "What is your name?"},
    ],
    dialogsData:[
        {id: 1, name: "Dima"},
        {id: 2, name: "Ilyas"},
        {id: 3, name: "Vanya"},
        {id: 4, name: "Sasha"},
        {id: 5, name: "Maximilian"}
    ],
    MessageText: "Hi",
};

const dialogsReducer = (state = initialState,action) => {

    let stateCopy;

    switch (action.type){
        case ADD_MESSAGE:
            stateCopy = {
                ...state,
                messagesData: [...state.messagesData]
            };
            let body = state.MessageText;
            stateCopy.MessageText = "";
            stateCopy.messagesData.push({id:4, messages: body});
            // let newMessage = {id:4, messages: state.MessageText};
            // stateCopy.messagesData.push(newMessage);
            return stateCopy;
        case UPDATE_NEW_MESSAGE_TEXT:
            stateCopy = {
                ...state,
                MessageText:action.newMessage
            };
            return stateCopy;
        default:
            return state;
    }
}

export let addMeassageActionCreater = () =>{
    return{type: ADD_MESSAGE}
} 

export let updateNewTextMessageActionCreater = (text) =>{
    return {type: UPDATE_NEW_MESSAGE_TEXT, newMessage:text}
}

export default dialogsReducer;
