import classes from './MyPosts.module.css';
import Post from './Post/Post';
import React from 'react'  ;
import { addPostActionCreater, updateNewTextActionCreater } from '../../../Redux/profile-reducer';

const MyPosts = (Data) => {

  let PostsData = Data.Data.Posts
                .map(post => <Post message={post.messages} like={post.likesCount} />)

  let newPostElement = React.createRef();

  let addPost = () => {
    Data.addPost();
    //  Data.dispatch(addPostActionCreater());
  }

  let onPostChange = () =>{
    let text = newPostElement.current.value;
    // let action = updateNewTextActionCreater(text);
    // Data.dispatch(action);
    Data.updataNewPostText(text);

  }

  return (
    <div className={classes.postsBlock}>
      <h3>My Post</h3>
      <div>
        <div>
          <textarea ref={newPostElement} onChange={onPostChange} 
                    value={Data.newPostText}>
          </textarea>
        </div>
        <div>
          <button onClick={addPost}>add post</button>
        </div>
      </div>
      <div className={classes.posts}>
        { PostsData }
      </div>
    </div>
  );
}

export default MyPosts;
