const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';

let initialState = {
    users:[
        {id: 1, photoUrl:"https://cdn.pixabay.com/photo/2017/01/31/21/23/avatar-2027366__340.png", followed: false, fullname:"Ilyas", status:"i'm a proger", location:{city:"Almaty", country:"Kazakhstan"}},
        {id: 2, photoUrl:"", followed: true, fullname:"Vladimir", status:"i'm a man", location:{city:"Moscow", country:"Russia"}},
        {id: 3, photoUrl:"", followed: false, fullname:"Ali", status:"Pypsik", location:{city:"Bishkek", country:"Kirgizstan"}},
        ],
    newPostText: "Hello World10",
};

const usersReducer = (state = initialState ,action) => {
    switch(action.type){
        case FOLLOW:
            return{
                ...state,
                users:state.users.map(u => {
                    if(u.id === action.userID){
                        return {...u, followed:true}
                    }
                    return u;
                })
            };
        case UNFOLLOW:
            return{
                ...state,
                users:state.users.map(u => {
                    if(u.id === action.userID){
                        return {...u, followed:false}
                    }
                    return u;
                })
            };
        case SET_USERS:{
            return {...state,users:[...state.users, ...action.users]};
        }
        default:
            return state;
    }
}

export const followAC = (userID) => {
    return{type: FOLLOW,userID}
  }
  
export  const unfollowAC = (userID) =>{
    return{ type: UNFOLLOW,userID}
}
export  const setUsersAC = (users) =>{
    return{ type: SET_USERS,users}
}
export default usersReducer;
