import MyPosts from './MyPosts';
import Post from './Post/Post';
import React from 'react'  ;
import { addPostActionCreater, updateNewTextActionCreater } from '../../../Redux/profile-reducer';

const MyPostsContainer = (Data) => {
  debugger
  let state = Data.state.getState();

  let addPost = () => {
     Data.store.dispatch(addPostActionCreater());
  }

  let onPostChange = (text) =>{
    let action = updateNewTextActionCreater(text);
    Data.store.dispatch(action);

  }

  return (<MyPosts updataNewPostText={onPostChange} 
                    addPost={addPost} 
                    post={state.profilePage.Posts}
                    newPostText = {state.profilePage.newPostText}/>);
}

export default MyPostsContainer;
