let rerenderEntireTree = () =>{
    console.log("good");
}

let state = {
    profilePage:{
        Posts:[
            {id: 1, messages: "Hi, how are you", likesCount: 15},
            {id: 2,messages: "it's my first post",likesCount: 20},
            ],
        newPostText: "Hello World10",
    },
    MessagesPage:{
        messagesData:[
            {id: 1, messages: "Hi"},
            {id: 2, messages: "How are you?"},
            {id: 3, messages: "What is your name?"},
        ],
        dialogsData:[
            {id: 1, name: "Dima"},
            {id: 2, name: "Ilyas"},
            {id: 3, name: "Vanya"},
            {id: 4, name: "Sasha"},
            {id: 5, name: "Maximilian"}
        ],
        MessageText: "Hi",
    }
}

export const addPost = () => {
    let newPost = {
        id:5,
        messages: state.profilePage.newPostText,
        likesCount:0,
    }

    state.profilePage.Posts.push(newPost);
    state.profilePage.newPostText = "";
    rerenderEntireTree(state);
};

export const UpdateNewPostText = (newText) => {
    state.profilePage.newPostText = newText;
    rerenderEntireTree(state);
};

export const addMessage = () => {
    let newMessage = {
        id:4,
        messages: state.MessagesPage.MessageText,
    }

    state.MessagesPage.messagesData.push(newMessage);
    state.MessagesPage.MessageText = "";
    rerenderEntireTree(state);
};

export const UpdateMessageText = (newMessage) => {
    state.MessagesPage.MessageText = newMessage;
    rerenderEntireTree(state);
};

export default state;
