import { NavLink } from 'react-router-dom';
import DialogItem from './DialogItem/DialogItem';
import classes from './Dialogs.module.css'
import Message from './Message/Message';
import React from 'react'  ;
import { addMeassageActionCreater, updateNewTextMessageActionCreater } from '../../Redux/dialogs-reducer';



const Dialogs = (props) =>{

    debugger;
    let state = props.dialogsData;

    let dialogsElements =state.dialogsData
            .map( dialog => <DialogItem name={dialog.name} id={dialog.id}/>);

    let messagesElements =state.messagesData
            .map( message => <Message message={message.messages}/>);

    let MessegeRef = React.createRef();

    let addMessage = () => {
        props.sendMessage();
    };

    let onMessageText = (e) =>{
        let body = e.target.value;
        props.upadeNewMessageBody(body)
      }

    return(
        <div className={classes.dialogs}>
            <div className={classes.dialogsItems}>
                { dialogsElements }
            </div>
            <div className={classes.messages}>
                { messagesElements }
                <textarea ref={MessegeRef} value={props.state.MessageText} onChange={onMessageText}></textarea>
                <button onClick={addMessage}>Send</button>
            </div>
            
        </div>
    );
}

export default Dialogs;
