            .map( dialog => <DialogItem name={dialog.name} id={dialog.id}/>);

    let messagesElements =Data.state.messagesData
            .map( message => <Message message={message.messages}/>);

    let MessegeRef = React.createRef();

    let addMessage = () => {
        Data.dispatch(addMeassage());
    };

    let onMessageText = () =>{
        let text = MessegeRef.current.value;
