import axios from "axios";
import React from "react";
import GetCookie from "../../hook/getCookie";
import c from './Profile.module.css'
import ProfileFirstBlock from "./ProfileFirstBlock/ProfileFirstBlock";
import ProfilePostsCategory from "./ProfilePostsCategory/ProfilePostsCategory";
import ProfilePostNwritePost from "./ProfilePostsFeatures/ProfilePostNwritePost";
import ProfilePostsFeaturesContainer from "./ProfilePostsFeatures/ProfilePostsFeaturesContainer";
import ProfileStoryBlock from "./ProfileStoryBlock/ProfileStoryBlock";
import { setUserProfile } from "../../Redux/posts-reducer";
import { connect } from 'react-redux';
import { withRouter } from "react-router-dom";
import ProfilePostsFeaturesContainerE from "./ProfilePostsFeatures/ProfilePostsFeaturesContainer";

class ProfileContainer extends React.Component{
  
  async componentDidMount(){
      await axios.get(`http://apeiron-test.kz/userAccess`,{
        headers:{
          Authorization: 'Bearer ' + JSON.parse(GetCookie('usrin'))['token']
        }
      }).then(response =>{
        this.props.setUserProfile(response.data);
      })
      let userId = this.props.match.params.userId;
      if(!userId){
        userId = 2;
      }
      // axios.get(`https://social-network.samuraijs.com/api/1.0/profile/` + userId)
      //   .then(response =>{
      //       this.props.setUserProfile(response.data);
      //   });
  }

  render(){
    return(
      <div className={c.Main}>
        <ProfileFirstBlock ProfileName={this.props.profile.name}
                            ProfileSecondName={this.props.profile.secondName}
                            ProfileStatus={this.props.profile.status}
                            ProfileEmail={this.props.profile.email}
                            ProfileLogin={this.props.profile.login}/>
        <ProfileStoryBlock/>
        <ProfilePostsCategory/>
        <ProfilePostsFeaturesContainerE ProfileLogin={this.props.profile.login}/>
    </div>
  )
  }
}

let mapStateToProps = (state) => {
  return{
      profile: state.ProfileData.profile
  }
} 

let WithUrlDataContainerComponent = withRouter(ProfileContainer);

export default connect(mapStateToProps,{setUserProfile})(WithUrlDataContainerComponent);
