import React from "react";
import styles from "./users.module.css"
import axios from "axios"; 
import userPhoto from "../../assets/img/defUserPng.jpg"

let Users = (props) => {

    if(props.users.length === 0){
        props.setUsers([
            {id:1,photoUser:"./assets/img/defUserPng",followed:"false",name:"Ilyas",status:"ok",location:{city:"amster",country:"hamster"}},
            {id:2,photoUser:"./assets/img/defUserPng",followed:"true",name:"Ils",status:"o",location:{city:"amst",country:"hter"}},
            {id:3,photoUser:"./assets/img/defUserPng",followed:"false",name:"Ilya",status:"ok",location:{city:"amer",country:"hamer"}}
        ])
    }
    

    return <div>
        {
            props.users.map(u => <div key={u.id}>
                <span>
                    <div>
                        <img src={userPhoto} className={styles.userPhoto} />
                    </div>
                    <div>
                        {u.followed ? <button onClick={() => {props.unfollow(u.id)}}>unFollow</button> : <button onClick={() => {props.follow(u.id)}}>Follow</button>}
                    </div>
                </span>
                <span>
                    <span>
                        <div>{u.name}</div>
                        <div>{u.status}</div>
                    </span>
                    <span>
                        <div>{"u.location.country"}</div>
                        <div>{"u.location.city"}</div>
                    </span>
                </span>
            </div>)
        }
    </div>
}
export default Users;


