import MyPosts from './MyPosts';
import React from 'react';
import { addPostActionCreater, updateNewTextActionCreater } from '../../../Redux/profile-reducer';
import StoreContext from '../../../StoreContext';

const MyPostsContainer = (props) => {



  let state = props.store.getState();

  let addPost = () => {
    props.store.dispatch(addPostActionCreater());
  }

  let onPostChange = (text) => {
    let action = updateNewTextActionCreater(text);
    props.store.dispatch(action);

  }

  return (
    <StoreContext.Consumer>
      <MyPosts updataNewPostText={onPostChange} addPost={addPost} posts={state.profilePage.Posts} newPostText={state.profilePage.newPostText}/>
    </StoreContext.Consumer>
    
  );
}

export default MyPostsContainer;
